--获取当前用户当日及当前商家未付款订单
set @OrderNo=null;
select top 1 @OrderNo=OrderNo from ma_order where BuyerId=@BuyerId and Status=@Status and DATEDIFF(day,CreateTime,GETDATE())=0 order by OrderNo desc

--如果不存在则创建一张新订单
if @OrderNo is null  begin
	set @SHour=
		left(replace(replace(replace(CONVERT(varchar, getdate(), 120),'-',''),' ','H'),':',''),11)+'-';

	set @OrderNo =
		@SHour+Right(
		(select IsNull(
			   Convert(
				  int,Right(Max(OrderNo),6)
			   ),0
			)+1000001
		  from 
			   ma_order 
		  where
			   Left(OrderNo,12)=@SHour),6
		);

	INSERT INTO ma_order (
	 [BuyerId],
	 [SellerId],
         [SellerStoreId],
	 [IsPay],
	 [PayAmount],
	 [Status],
	 [ReceiveCountry],
	 [ReceiveProvince],
	 [ReceiveCity],
	 [ReceiveDistrict],
	 [ReceiveName],
	 [ReceiveTel],
	 [ReceiveZip],
	 [ReceiveDetails],
	 [CreateTime],
	[OrderNo])
	 VALUES (
	@BuyerId,
	@SellerId,
        @SellerStoreId,
	@IsPay,
	@PayAmount,
	@Status,
	@ReceiveCountry,
	@ReceiveProvince,
	@ReceiveCity,
	@ReceiveDistrict,
	@ReceiveName,
	@ReceiveTel,
	@ReceiveZip,
	@ReceiveDetails,
	@CreateTime,
	@OrderNo);

	--设置默认送货地址
	if(select top 1 UserId from ma_my_setting where UserId=@BuyerId) is null 
	--如果设置了默认地址
	begin
		update ma_order set
		 [ReceiveCountry]=b.Country,
		 [ReceiveProvince]=b.Province,
		 [ReceiveCity]=b.City,
		 [ReceiveDistrict]=b.District,
		 [ReceiveName]=b.Receiver,
		 [ReceiveTel]=b.Tel,
		 [ReceiveZip]=b.Zip,
		 [ReceiveDetails]= b.Details
		from 
		 ma_user_address  b left join  ma_my_setting 
		 on  b.UserId=ma_my_setting.UserId
			 and b.UserId=@BuyerId
		where
		  b.UserId=@BuyerId and
		  ma_order.OrderNo=@OrderNo;
    end		  
    else
    begin	
    --否则
		update  ma_order set
		 [ReceiveCountry]=b.Country,
		 [ReceiveProvince]=b.Province,
		 [ReceiveCity]=b.City,
		 [ReceiveDistrict]=b.District,
		 [ReceiveName]=b.UserId,
		 [ReceiveTel]=b.Tel,
		 [ReceiveZip]=b.Zip,
		 [ReceiveDetails]= b.Receiver
		from 
		  ma_user_address  b 
		where
		  b.UserId=@BuyerId and
		  ma_order.OrderNo=@OrderNo;
	end	  
end

update ma_order_details set
 [OrderNo]=@OrderNo,
 [GoodsId]=@GoodsId,
 [GoodsName]=@GoodsName,
 [Model]=@Model,
 [Price]=@Price,
 [OrgPrice]=@OrgPrice,
 [Count]=@Count,
 [Amount]=@Price*@Count,
 [Attrs]=@Attrs
where
 [OrderNo]=@OrderNo and
 [GoodsId]=@GoodsId and
 [GoodsName]=@GoodsName and
 IsNull([Model],'')=IsNull(@Model,'') and
 IsNull([Attrs],'')=IsNull(@Attrs,'');

IF @@ROWCOUNT = 0 
begin
--插入商品明细数据
INSERT INTO ma_order_details (
 [Id],
 [OrderNo],
 [GoodsId],
 [GoodsName],
 [Model],
 [Price],
 [OrgPrice],
 [Count],
 [Amount],
[Attrs])
 VALUES (
@DetailsId,
@OrderNo,
@GoodsId,
@GoodsName,
@Model,
@Price,
@OrgPrice,
@Count,
@Count*@Price,
@Attrs)
end;

update ma_order 
   set
      PayAmount=(select sum(Amount)  from ma_order_details where OrderNo=@OrderNo)
Where OrderNo=@OrderNo;

select * from ma_order_details    where
 [OrderNo]=@OrderNo and
 [GoodsId]=@GoodsId and
 [GoodsName]=@GoodsName and
 [Model]=@Model and
 [Attrs]=@Attrs;
